INSERT INTO `dede_archives` VALUES ('12', '2', '0', '1294124410', 'p', '-1', '1', '-2', '112', '0', '5月6日晚传灯法会和联欢晚会', '', '', 'admin', '未知', '/uploads/allimg/110104/1501542A4-0-lp.jpg', '1294124410', '1294124514', '1', '法会,联欢晚会,传灯,日晚,6,5月,', '0', '0', '0', '0', '0', '在这样一个殊胜、祥和之夜； 一个动人心魄感人肺腑的夜晚， 我们用了最好的语言， 最美的歌声也表达不尽佛陀给予我们的 恩赐 。 传灯法会开始 点亮心中的明灯，让我们的前途无限光明', '', '1', '0', '0', '0', '11');
INSERT INTO `dede_archives` VALUES ('13', '21', '0', '1294124518', 'p', '-1', '1', '-2', '86', '0', '第三天－－开示', '', '', 'admin', '未知', '/uploads/allimg/110104/1_010415033451C.jpg', '1294124518', '1294124614', '1', '开示,--,第三天,', '0', '0', '0', '0', '0', '第二届智慧人生学修营，佛心法师为带来了以下五堂开示: 一、正信与迷信。 二、信的层次。 三、启迪人生智慧的格言。 四、活出希望。 五、自在的人生。 佛心法师简历 佛心法师，法名佛心，字法诠，湖南临武县人。一九九一年于韶关南华禅寺，礼上传下辉法师，', '', '1', '0', '0', '0', '12');
INSERT INTO `dede_archives` VALUES ('14', '34', '0', '1294124684', '', '-1', '1', '0', '149', '0', '全球观音大法会30日在马来西亚吉隆坡揭幕', '', '', 'admin', '未知', '', '1294124684', '1294124694', '1', '马来西亚,吉隆坡,揭幕,30日,会,观音,大法,全球,', '0', '0', '0', '0', '0', '经过一整年的筹备，由马来西亚全国佛团寺院联合举办的全球观音大法会于２００７年７月３０日在吉隆坡武吉加里尔国家体育馆正式揭幕。 来自马来西亚国内外的南北藏传高僧大德受邀出席，当晚并恭请国际佛光会中华总会总会长心定法师主持慈悲传灯法会，有三万信', '', '1', '0', '0', '0', '13');
INSERT INTO `dede_archives` VALUES ('15', '34', '0', '1294124696', '', '-1', '1', '0', '58', '0', '藏传佛教弘传历史艺术文化专题研讨会召开', '', '', 'admin', '未知', '', '1294124696', '1294124740', '1', '研讨会,召开,专题,艺术文化,弘传,历史,藏传佛教,', '0', '0', '0', '0', '0', '藏传佛教后弘期上路弘传历史艺术文化专题研讨会在北京中国藏学研究中心召开，历时三天。本次会议由中国藏学研究中心历史研究所承办。中国藏学研究中心拉巴平措总干事、奥地利维也纳大学的戴伯拉克林博格索特教授分别在开幕式上发表了热情洋溢的讲话。 来自奥', '', '1', '0', '0', '0', '14');
INSERT INTO `dede_archives` VALUES ('16', '34', '0', '1294124748', '', '-1', '1', '0', '94', '0', '厦门南普陀慈善会六一儿童节前夕慰问孤儿', '', '', 'admin', '未知', '', '1294124748', '1294124755', '1', '前夕,慰问,孤儿,六一儿童节,会,南,普陀,慈善,厦门,', '0', '0', '0', '0', '0', '５月２６日，南普陀寺慈善会的普陀法师、德鸣法师、慧暄法师、通灵法师前往厦门市思明区、翔安区资助１００名孤儿，每名６００元，共计６万元，并为孤儿送去了牛奶等慰问品，祝愿孤儿过上一个愉快的节日，希望孤儿好好学习，健康成长。', '', '1', '0', '0', '0', '15');
INSERT INTO `dede_archives` VALUES ('17', '34', '0', '1294124767', '', '-1', '1', '0', '156', '0', '辽宁大学永惺佛学研究中心揭牌仪式29日举行', '', '', 'admin', '未知', '', '1294124767', '1294124779', '1', '仪式,29,日举行,揭牌,研究中心,大学,永惺,佛学,辽宁,', '0', '0', '0', '0', '0', '２００７年５月２９日，辽宁大学永惺佛学研究中心揭牌仪式在辽宁大学蒲河校区举行。该研究中心是由辽宁大学、香港西方寺、朝阳佛教学会三方合作创立。国内著名佛学研究专家、中国人民大学宗教研究所所长方立天教授出席揭牌仪式并作学术报告。', '', '1', '0', '0', '0', '16');
INSERT INTO `dede_archives` VALUES ('18', '34', '0', '1294124789', '', '-1', '1', '0', '117', '0', '中国驻斯里兰卡大使会见斯里兰卡僧侣党书记', '', '', 'admin', '未知', '', '1294124789', '1294124797', '1', '斯里兰卡,书记,僧侣党,大使,中国驻,会见,', '0', '0', '0', '0', '0', '２００７年５月２９日，叶大波大使会见斯里兰卡议会政党僧侣党总书记索比塔高僧和在斯留学的中国少林寺延印法师，就两国佛教界交往交换了意见。斯总统府秘书处官员也参加了会见。 索比塔介绍了斯里兰卡佛教界的情况，表示两国佛教界的交往历史悠久，希望今后', '', '1', '0', '0', '0', '17');
INSERT INTO `dede_archives` VALUES ('19', '34', '0', '1294124810', '', '-1', '1', '0', '55', '0', '星云大师捐千万给岳麓书院 建中国书院博物馆', '', '', 'admin', '未知', '', '1294124810', '1294124818', '1', '中国,书院,博物馆,建,岳麓书院,大师,捐,千万,星云,', '0', '0', '0', '0', '0', '４月１６日上午，台湾佛光山星云大师在湖南岳麓书院举行的明伦堂讲会中表示，他将捐献一千万元人民币给岳麓书院，用来建设中国书院博物馆，希望这笔捐款能起到抛砖引玉的作用。 星云大师说，建设中国书院博物馆是一件很有意义的事情。一千多年前，岳麓书院建', '', '1', '0', '0', '0', '18');
INSERT INTO `dede_archives` VALUES ('20', '34', '0', '1294124826', 'p', '-1', '1', '0', '70', '0', '中韩日三国第十次佛教友好交流会达成共识', '', '', 'admin', '未知', '/uploads/allimg/110104/1_0104150S31M2.jpg', '1294124826', '1294124913', '1', '友好,交流会,达成,共识,佛教,次,韩日,三国,第十,中,', '0', '0', '0', '0', '0', '４月１４日讯 ２００７年４月１２日～１３日，第十次中韩日佛教友好交流会议北京大会预备会议在北京召开。 １３日的预备会议由中国佛教协会副会长兼秘书长学诚法师主持，中、韩、日三国佛教代表就三件事达成一致共识：１、关于召开第十次中韩日佛教友好交流', '', '1', '0', '0', '0', '19');
INSERT INTO `dede_archives` VALUES ('21', '34', '0', '1496586820', 'p', '-1', '1', '0', '133', '0', '1扬州鉴真佛学院相关人员到重庆佛学院调研21', '', '', 'admin', '未知', '/uploads/allimg/110104/1_01041509204A9.jpg', '1496586820', '1294124960', '1', '佛,学院,重庆,调研,人员,相关,扬州,鉴真,', '0', '0', '0', '0', '0', '２００７年４月１８日上午，江苏省扬州市民宗局顾处长、鉴真佛学院殷炳建副院长、永兴教务长、扬州大明寺能度监院一行，到重庆华岩寺进行调研工作。 在重庆市民宗委调研员张勇、重庆佛学院教务长李正思居士的陪同下，顾处长一行游览了华岩金佛、弘法楼、重庆', '', '1', '0', '0', '0', '20');
INSERT INTO `dede_archives` VALUES ('24', '34', '0', '1294125086', 'p', '-1', '1', '0', '129', '0', '北京宗教界代表举座谈会深切怀念傅铁山主教', '', '', 'admin', '未知', '/uploads/allimg/110104/1_0104151204N38.jpg', '1294125086', '1294125124', '1', '怀念,傅铁山,主教,深切,座谈会,宗教界,代表,举,北京,', '0', '0', '0', '0', '0', '２００７年５月１６日，北京市天主教、基督教、佛教、道教、伊斯兰教界代表人士３０余人举行座谈会，深切怀念杰出的爱国宗教领袖、著名的社会活动家、中国共产党的亲密朋友、第十届全国人民代表大会常务委员会副委员长、天主教北京教区主教傅铁山。会议由市', '', '1', '0', '0', '0', '23');
INSERT INTO `dede_archives` VALUES ('25', '2', '0', '1294125265', '', '-1', '1', '-2', '191', '0', '香港宝莲禅寺举行浴佛节典礼和佛诞法会', '', '', 'admin', '未知', '', '1294125265', '1294125274', '1', '典礼,佛诞,法会,节,浴佛节,宝莲,禅寺,举行,香港,', '0', '0', '0', '0', '0', '２００７年５月２０日，香港宝莲禅寺举行盛大浴佛节典礼和佛诞法会。中央人民政府驻港联络办主任高祀仁、全国人大常委曾宪梓、香港立法会主席范徐丽泰、香港特区政府经济及劳工局局长叶澍堃参加浴佛典礼。 宝莲禅寺住持智慧法师表示，宝莲寺每年都举行浴佛节', '', '1', '0', '0', '0', '24');
INSERT INTO `dede_archives` VALUES ('26', '2', '0', '1294125635', '', '-1', '1', '-2', '159', '0', '少林寺、观音寺将传授三坛大戒', '', '', 'admin', '未知', '', '1294125635', '1294125650', '1', '大戒,三坛,传授,观音寺,少林寺,', '0', '0', '0', '0', '0', '今年５月２４日至６月２１日，登封少林寺暨商丘观音寺将传授三坛大戒。 截至２００６年年底，河南已有５００余处寺院作为宗教活动场所开放。但１９９６年洛阳白马寺传授三坛大戒至今，省佛教开放寺院没有再举行过传戒法会。 １０年来，每年河南佛教界都有近', '', '1', '0', '0', '0', '25');
INSERT INTO `dede_archives` VALUES ('27', '21', '0', '1294125662', '', '-1', '1', '-2', '164', '0', '千年古刹 千年胜会', '', '', 'admin', '未知', '', '1294125662', '1294125675', '1', '千年,胜会,古刹,', '0', '0', '0', '0', '0', '福建省古田县极乐寺 首 届 水陆胜会通告 水陆法会是中国佛教中仪式最隆重、功德最殊胜的法会，全称是法界圣凡水陆普度大斋胜会道场。 水陆法会起源于梁朝（西元 502 年），蕭衍（武帝）为度郗氏娘娘，恳请神僧宝志禅师，聘五百高僧创建水陆法会，此乃始兴水', '', '1', '0', '0', '0', '26');
INSERT INTO `dede_archives` VALUES ('28', '2', '0', '1294125683', '', '-1', '1', '-2', '96', '0', '网络佛子迎新春联欢晚会', '', '', 'admin', '未知', '', '1294125683', '1294125699', '1', '联欢晚会,新春,迎,佛,网络,', '0', '0', '0', '0', '0', '2007年元月23日晚上8时，圣域佛教举办新春联欢晚会，届时恭请各位光临。 地点：圣域佛教圣水寺网络授课室（ http://www.heshang.net/ ）', '', '1', '0', '0', '0', '27');
INSERT INTO `dede_archives` VALUES ('29', '2', '0', '1294125773', 'p', '-1', '1', '-2', '143', '0', '极乐寺水陆法会图片((外坛--法华坛)', '', '', 'admin', '未知', '/uploads/allimg/110104/1523041021-0-lp.jpg', '1294125773', '1294125784', '1', '--,法华,坛,外坛,图片,水陆,法会,极乐寺,', '0', '0', '0', '0', '0', '水陆法会外坛--法华坛(图一) 水陆法会外坛--法华坛(图二)', '', '1', '0', '0', '0', '28');
INSERT INTO `dede_archives` VALUES ('30', '21', '0', '1294125995', 'p', '-1', '1', '-2', '120', '0', '极乐寺水陆法会图片((内坛)', '', '', 'admin', '未知', '/uploads/allimg/110104/1_0104152U611L.jpg', '1294125995', '1294126136', '1', '内坛,图片,法会,水陆,极乐寺,', '0', '0', '0', '0', '0', '内坛大佛(图一) 内坛大佛(图一) 内坛韦陀菩萨 内坛伽蓝菩萨 内坛左侧(图一) 内坛左侧(图一) 内坛左侧(图一) 内坛右侧(图二) 内坛右侧(图二)', '', '1', '0', '0', '0', '29');
INSERT INTO `dede_archives` VALUES ('31', '2', '0', '1294126406', 'p', '-1', '1', '-2', '165', '0', '第三届“智慧人生”学修营活动通启', '', '', 'admin', '未知', '/uploads/allimg/110104/1533463634-0-lp.jpg', '1294126406', '1294126426', '1', '活动,通启,营,学修,智慧人生,第三届,', '0', '0', '0', '0', '0', '福建省古田县极乐寺 第三届智慧人生学修营活动通启 缘起： 处在喧闹躁动的世间，面对纷纭复杂的人事，也许你正经历情感的困扰而惶惑凄然；或许你正遭遇工作的挫折而烦恼不堪；抑或你正面临生活的失意而忧心如焚 烦恼暗宅中，常需生慧日，我们相约国庆，走进', '', '1', '0', '0', '0', '30');
INSERT INTO `dede_archives` VALUES ('32', '2', '0', '1294126444', 'p', '-1', '1', '-2', '131', '0', '福建古田极乐寺募资铸造钟缘起', '', '', 'admin', '未知', '/uploads/allimg/110104/15342L423-0-lp.jpg', '1294126444', '1294126467', '1', '铸造,钟缘起,募资,极乐寺,古田,福建,', '0', '0', '0', '0', '0', '娑婆世界，多音声为佛事，丛林法器，以大钟为第一。如百丈清规中说：大钟丛林号令资始也。晓击即破长夜，警睡眠；暮击则觉昏衢，疏冥昧。寺院中朝时先钟后鼓，夜晚先鼓后钟，钟敲鹤起，鼓击龙飞，惊醒世间名利客，唤回苦海梦迷人。是以世人常借晨钟暮鼓四字', '', '1', '0', '0', '0', '31');
INSERT INTO `dede_archives` VALUES ('33', '21', '0', '1294126475', '', '-1', '1', '-2', '135', '0', '一堂一千八百万元的课', '', '', 'admin', '未知', '', '1294126475', '1294126490', '1', '课,万元,千八百,一,一堂,', '0', '0', '0', '0', '0', '一位朋友谈到了他的遭遇，约在十多年前，他因为信任，借了朋友一千八百万元周转，这位多年深交的好友是生意人，事业做得很大，住豪宅，出入有名车，只是二个月后，这位「好朋友」从人间消失了，完全找不到讯息，听说为了躲债跑到美国去了，而那一千八百万瞬', '', '1', '0', '0', '0', '32');
INSERT INTO `dede_archives` VALUES ('34', '21', '0', '1294129675', '', '-1', '1', '-2', '187', '0', '小虫的启示', '', '', 'admin', '未知', '', '1294129675', '1294129685', '1', '启示,小虫,', '0', '0', '0', '0', '0', '我是一名职业校对员，曾经校对过北美航空公司的《飞行员手册》、联邦基金会的《职业教育研究报告》和数不清的刊物。生活中，我也是个不可救药的挑错专家。看书时，我会不自觉地检查单词拼写和标点符号是否准确。听别人讲话时，我总在考虑他的发音是否正确，', '', '1', '0', '0', '0', '33');
INSERT INTO `dede_archives` VALUES ('35', '21', '0', '1294129693', '', '-1', '1', '-2', '99', '0', '此爱绵绵', '', '', 'admin', '未知', '', '1294129693', '1294129701', '1', '绵绵,此爱,', '0', '0', '0', '0', '0', '一个大学二年级男生，在校外游泳时，不幸溺水身亡。死时年仅二十岁。事情非常突然。学校电告他家中，请速速来人。几天后，学校在火车站上只接到一位白发苍苍的老太太和一个十四岁的小孩儿，两人穿戴极为破旧。原来死者的父亲也已于大学生出事的前两天离开了', '', '1', '0', '0', '0', '34');
INSERT INTO `dede_archives` VALUES ('36', '21', '0', '1294129708', '', '-1', '1', '-2', '180', '0', '洗碗的小沙弥', '', '', 'admin', '未知', '', '1294129708', '1294129717', '1', '弥,小沙,洗碗,', '0', '0', '0', '0', '0', '一行禅师过去在越南的深山里面出家，也是在一个大庙里。那边的庙里，有一件苦恼的事情，１０ 多岁出家一个小沙弥在大庙里面，每天都要洗碗。他每一次都觉得这个洗碗是一件非常辛苦的事情：我出家是来修行的，怎么每天都在洗碗呢？他觉得洗碗跟修行没有关系，', '', '1', '0', '0', '0', '35');
INSERT INTO `dede_archives` VALUES ('37', '21', '0', '1294129727', '', '-1', '1', '-2', '112', '0', '金表', '', '', 'admin', '未知', '', '1294129727', '1294129738', '1', '金表,', '0', '0', '0', '0', '0', '这是一个真实的故事。 两年前，在我国东北某市，发生了一起盗窃案:某高 档商场被盗，其中有8块金表，每块价值8万余元。 就在案子尚未侦破时，外地的一位商人到此地进货， 随身携带了近10万元巨款。早晨下飞机住到酒店后，他先去办理了贵重物品保存手续，将钱', '', '1', '0', '0', '0', '36');
INSERT INTO `dede_archives` VALUES ('38', '21', '0', '1294129766', '', '-1', '1', '-2', '197', '0', '禅与经商', '', '', 'admin', '未知', '', '1294129766', '1294129775', '1', '经商,禅,', '0', '0', '0', '0', '0', '有一位朋友找我聊天，说他经商多年来通过努力拼搏赚到了不少钱，在同行中也算是个大老板，但商场如战场，他越来越觉得筋疲力尽，心力憔悴，心情烦躁。感慨了一番之后他问我怎么办？ 莫把商场当战', '', '1', '0', '0', '0', '37');
INSERT INTO `dede_archives` VALUES ('39', '21', '0', '1294129784', '', '-1', '1', '-2', '53', '0', '八度幸福', '', '', 'admin', '未知', '', '1294129784', '1294129793', '1', '幸福,八度,', '0', '0', '0', '0', '0', '去过很多旅游景点，留给我印象最深的不是名山大川的恢宏气势，也不是小桥流水的雅致幽静；不是万丈高楼的流光溢彩，也不是摩肩接踵人群的潮流时尚，而是一个并不出名的景点草堂门口的一块杉木牌。那个牌子上刻着这样一段话： 如先改变自己，对方也会改变；对', '', '1', '0', '0', '0', '38');
INSERT INTO `dede_archives` VALUES ('40', '21', '0', '1294129799', '', '-1', '1', '-2', '106', '0', '平凡与超凡', '', '', 'admin', '未知', '', '1294129799', '1294129808', '1', '超凡,凡与,平,', '0', '0', '0', '0', '0', '平凡与超凡犯错是平凡的，而知错能改是超凡的。 每个人都会做错事，但是有的人就会知错就改，而有的人却执迷不悟。佛教里面有戒律，世间上有法律。首先来说说我们出家人的戒律吧!它可分重戒和轻戒两种，它是用来防非止恶的，防止我们身口意所造的恶业，我们', '', '1', '0', '0', '0', '39');
INSERT INTO `dede_archives` VALUES ('41', '21', '0', '1294129814', '', '-1', '1', '-2', '52', '0', '无声的说法', '', '', 'admin', '未知', '', '1294129814', '1294129823', '1', '说法,无声,', '0', '0', '0', '0', '0', '走向学佛之路 ◎证严法师 无所求的奉献，及为一切众生而修养自己的言语行动，就是学佛。 佛陀的教育不只是教我们如何了生脱死，更是教我们如何去包容人，不生计较。 学佛要修养到无论发生什么事，心中没有丝毫委屈感。不先培养爱心和耐心，则学佛难成。学佛', '', '1', '0', '0', '0', '40');
INSERT INTO `dede_archives` VALUES ('42', '21', '0', '1294129831', '', '-1', '1', '-2', '180', '0', '人间佛教的修持', '', '', 'admin', '未知', '', '1294129831', '1294129841', '1', '修持,佛教,人间,', '0', '0', '0', '0', '0', '人间佛教的修持 学佛，就是向佛学习，以佛为我们的模范而学。佛是怎样修学而成的，我们也这样照着学。学佛，是离不了修持的。持是受持，是择善而固执之的意思。修是熏修，是依著受持的佛法去学习。照中文的训释，如修身、修理等，修有改正的意思。依佛法，修', '', '1', '0', '0', '0', '41');
INSERT INTO `dede_archives` VALUES ('43', '21', '0', '1294129850', '', '-1', '1', '-2', '186', '0', '出家人对社会的贡献', '', '', 'admin', '未知', '', '1294129850', '1294129861', '1', '贡献,社会,出家人,', '0', '0', '0', '0', '0', '索达吉堪布讲述 如今在社会上很多人对出家人特别轻视，好像在他们的眼目中，出家人都是因为人生不得意受到各种打击或者是生活上走投无路，才选择逃避现实出家为僧这条灰色道路。其实这正表明他们根本不懂得历史，我们且不说出家人本身所具有的功德及其内在的', '', '1', '0', '0', '0', '42');
INSERT INTO `dede_archives` VALUES ('44', '21', '0', '1294129869', '', '-1', '1', '-2', '60', '0', '怎样做一个居士', '', '', 'admin', '未知', '', '1294129869', '1294129888', '1', '居士,一个,做,怎样,', '0', '0', '0', '0', '0', '圣严法师 目录 《一》三类法门 《二》解脱道与菩萨道 《三》居士宜修菩萨道 《四》菩萨道的重心 《五》居士应具的条件 《六》居士的家庭生活 《七》居士的社会生活 《八》居士的宗教生活 在家人信仰了佛教，通常被称为居士。 那么，做一个居士，跟普通的在家', '', '1', '0', '0', '0', '43');
